import React, { useState, useEffect, useCallback } from "react";

import { Container, Content, Score, Image } from "./MemoryGame.style";

import fries from "../../assets/images/fries.png";
import cheeseburger from "../../assets/images/cheeseburger.png";
import icecream from "../../assets/images/ice-cream.png";
import pizza from "../../assets/images/pizza.png";
import milkshake from "../../assets/images/milkshake.png";
import hotdog from "../../assets/images/hotdog.png";
import blank from "../../assets/images/blank.png";
import white from "../../assets/images/white.png";

const cardArray = [
  {
    name: "fries",
    img: fries,
  },
  {
    name: "cheeseburger",
    img: cheeseburger,
  },
  {
    name: "ice-cream",
    img: icecream,
  },
  {
    name: "pizza",
    img: pizza,
  },
  {
    name: "milkshake",
    img: milkshake,
  },
  {
    name: "hotdog",
    img: hotdog,
  },
  {
    name: "fries",
    img: fries,
  },
  {
    name: "cheeseburger",
    img: cheeseburger,
  },
  {
    name: "ice-cream",
    img: icecream,
  },
  {
    name: "pizza",
    img: pizza,
  },
  {
    name: "milkshake",
    img: milkshake,
  },
  {
    name: "hotdog",
    img: hotdog,
  },
];
cardArray.sort(() => 0.5 - Math.random());

const MemoryGame = () => {
  const [cardsChosen, setCardsChosen] = useState([]);
  const [cardsChosenId, setCardsChosenId] = useState([]);
  const [cardsWonId, setCardsWonId] = useState([]);

  useEffect(() => {
    const checkForMatch = () => {
      const [cardA, cardB] = cardsChosen;
      const [idA, idB] = cardsChosenId;

      if (idA === idB) {
        console.log("You have clicked the same image!");
      } else if (cardA.name === cardB.name) {
        console.log("You found a match");
        setCardsWonId((oldCardsWonId) => [...oldCardsWonId, ...cardsChosenId]);
      } else {
        console.log("Sorry, try again");
      }
      setCardsChosen([]);
      setCardsChosenId([]);
    };

    if (cardsChosen.length === 2) {
      setTimeout(checkForMatch, 500);
    }

    return () => {};
  }, [cardsChosen, cardsChosenId]);

  const renderBoard = () => {
    return cardArray.map((data, index) => {
      return (
        <Image
          key={index}
          src={
            cardsChosenId.includes(index)
              ? data.img
              : cardsWonId.includes(index)
              ? white
              : blank
          }
          onClick={cardsWonId.includes(index) ? null : () => flipCard(index)}
        />
      );
    });
  };

  const flipCard = (index) => {
    setCardsChosenId((oldChosenId) => [...oldChosenId, index]);
    setCardsChosen((oldCardsChosen) => [...oldCardsChosen, cardArray[index]]);
  };

  const renderScore = () => {
    return cardsWonId.length === cardArray.length ? (
      <Score>Congratulations! You found them all!</Score>
    ) : (
      <Score>
        Score: <span>{cardsWonId.length / 2}</span>
      </Score>
    );
  };

  return (
    <Container>
      {renderScore()}
      <Content>{renderBoard()}</Content>
    </Container>
  );
};

export default MemoryGame;
